import React, {useState, useRef} from 'react';

// this number might need adjusting for dynamicness
const scrollToRef = () => window.scrollTo(0, 850) 

const TeamMembercard = ({teamMember}) => {
    //checks weather on of the cards is toggled
    const [toggle, setToggle] = useState(false);

    //checks the current card
    const [classList, setClassList] = useState("profile col-lg-4 col-md-6 col-sm-6 col-8");

    //toggles the previous card selected
    const [prevClass, setPrevClass] = useState("profile col-lg-4 col-md-6 col-sm-6 col-8");

    const scrollRef = useRef(null);

    const executeScroll = () => scrollToRef(650)

    // this will take the boxshadow and the bottom border off the card
    // and display the coresponding info 
    const dropCardAndDisplayInfo = () => {
        if(toggle){
            setClassList("active-profile order-12 col-lg-12 col-md-12 col-sm-12 col-12");
            setPrevClass("active-profile order-12 col-lg-12 col-md-12 col-sm-12 col-12");
        }
        setToggle(true);
        executeScroll();
    }

    return (
        <div key={teamMember.member_id} ref={scrollRef} className={classList}>
            <h1>{teamMember.name}</h1>
            <img className={teamMember.class} src={teamMember.img} alt={teamMember.name}/>
            <button className="link-text btn" onClick={dropCardAndDisplayInfo} >
                <h3 >Meet {teamMember.fName}</h3>
            </button>
        </div>
    );
}

export default TeamMembercard;
