import React, {useState, useRef} from 'react';


const scrollToRef = (scrollRef) => window.scrollTo(0, ref.current.offsetTop) 

const TeamMembercard = ({teamMember}) => {
    const [classList, setClassList] = useState("profile col-lg-4 col-md-6 col-sm-6 col-8");

    const scrollRef = useRef(null);


    // this will take the boxshadow and the bottom border off the card
    // and display the coresponding info 
    const dropCardAndDisplayInfo = () => {
        setClassList("active-profile col-lg-12 col-md-12 col-sm-12 col-12");
        scrollToRef();
    }

    return (
        <div key={teamMember.member_id} ref={scrollRef} className={classList}>
            <h1>{teamMember.name}</h1>
            <img className={teamMember.class} src={teamMember.img} alt={teamMember.name}/>
            <a className="link-text" onClick={dropCardAndDisplayInfo} >
                <h3 >Meet {teamMember.fName}</h3>
            </a>
        </div>
    );
}

export default TeamMembercard;
