import React, {useState, useRef} from 'react';
import ReactCardFlip from 'react-card-flip';


const TeamMembercard = ({teamMember}) => {

    //checks weather on of the cards is toggled
    const [isFlipped, setIsFlipped] = useState(false);



    // this will take the boxshadow and the bottom border off the card
    // and display the coresponding info 
    const handleClick = () => {
        setIsFlipped(!isFlipped);

    }


    return (
        <ReactCardFlip isFlipped={isFlipped} flipDirection="vertical">
            <div key={teamMember.member_id} className="profile col-lg-4 col-md-6 col-sm-6 col-8">
                <h1>{teamMember.name}</h1>
                <img className={teamMember.class} src={teamMember.img} alt={teamMember.name}/>
                <button className="link-text btn" onClick={handleClick} >
                <h3 >Meet {teamMember.fName}</h3>
                </button>
            </div>

            <div className="container-fluid mt-5">
                {/* where member info will be rendered*/}
                <div class="about">
                    <h3>About Harrison Newman</h3>
                    <p>My name is Harrison and I'm in my fourth year at Liberty. I am a Commercial/Corporate Aeronautics Major with a minor in Aviation Safety. I hope to one day become an airline pilot. I am a huge baseball fan and love the New York Yankees, I love to do outdoor activities like going to LMGC or playing beach volleyball. I am excited to see how god moves on this hall this year!</p>
                </div>
                <div class="testimony">
                    <img src="/images/harrison-newman-about-pic.jpg" alt="about-pic" />
                    <h3>Harrison's testimony</h3>
                    <p>I grew up in a very loving family living in a few places, Rhode Island, Hawaii, Virginia Beach, and now Washington DC. Through the years we did go to church for a bit but I never took my faith seriously and while I was quite happy it always feel like something was missing. When I came to liberty I felt immediately welcomed by my hall and wondered what made them different than other people I had been around my whole life. I wanted to know what gave them their purpose, through that whole experience I came to know Christ at 3:00 a.m. on a Thursday morning after groups. It was the best decision I ever made in my life and still am growing in my faith daily. I am always willing to share more about how God is still growing and molding me to be more like Jesus. feel free to contact me at any time!</p>
                </div>
            </div>
        </ReactCardFlip>


    );
}

export default TeamMembercard;
