import React,{ Component } from "react";


class PostForm extends Component {

    constructor(props) {
        super();
        this.inputText = React.createRef();
        this.state = {
            file : null
        };
    }


    getFile = e => {
        e.preventDefault();
        let imgFile = e.target.files[0];
        console.log(imgFile);
        this.setState({file: imgFile});
        e.target.value = imgFile;
    }

    // On file upload (click the upload button) 
    onFileUpload = () => { 

        // Create an object of formData 
        const formData = new FormData(); 

        // Update the formData object 
        formData.append( 
          "myFile", 
          this.state.selectedFile, 
          this.state.selectedFile.name 
        ); 

        // Details of the uploaded file 
        console.log(this.state.selectedFile); 

        // Request made to the backend api 
        // Send formData object 
        // axios.post("api/uploadfile", formData); 
      }; 

    render() {
        return (
            <div>
                <div className="row justify-content-center">
                    <div className="col-3 ">
                        <form>
                            {/* author */}
                            <div className="form-group">
                                <label htmlFor="author">Author</label>
                                <input type="text" className="form-control" placeholder="Enter your name" />
                            </div>
                            {/* caption */}
                            <div className="form-group">
                                <label htmlFor="caption">Caption</label>
                                <input type="text" className="form-control" placeholder="Caption" />
                            </div>

                            {/* img to post */}
                            <div className="custom-file mt-5">
                                <input type="file" className="custom-file-input" onChange={this.getFile} />
                                <label ref={this.inputText} className="custom-file-label file-text" htmlFor="customFile">Choose file</label>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }


}





  export default PostForm;
